import React, { useState } from "react";
import axios from "axios";
import { MessageList } from "react-chat-elements";

function App() {
  const [pdfId, setPdfId] = useState(null);
  const [question, setQuestion] = useState("");
  const [chat, setChat] = useState([]);

  const uploadPDF = async (e) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      setPdfId(response.data.pdfId);
      setChat([
        ...chat,
        { role: "system", content: "PDF uploaded successfully." },
      ]);
    } catch (error) {
      console.error("Error uploading PDF:", error);
    }
  };

  const askQuestion = async () => {
    if (!pdfId) {
      alert("Please upload a PDF first.");
      return;
    }

    try {
      const response = await axios.post("/ask", { question, pdfId });
      setChat([
        ...chat,
        { role: "user", content: question },
        { role: "bot", content: response.data.answer },
      ]);
      setQuestion("");
    } catch (error) {
      console.error("Error asking question:", error);
    }
  };

  return (
    <>
      <div>
        <h1>Chat with PDF</h1>
        <input type="file" onChange={uploadPDF} />
        <br />
        <textarea
          rows="20"
          cols="100"
          readOnly
          value={chat
            .map((c) => `${c.role === "user" ? "You" : "Bot"}: ${c.content}`)
            .join("\n")}
        />
        <br />
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Ask a question"
        />
        <button onClick={askQuestion}>Ask</button>
      </div>
      <MessageList
        className="message-list"
        lockable={true}
        toBottomHeight={"100MessageList%"}
        dataSource={[
          {
            position: "left",
            type: "text",
            title: "Kursat",
            text: "Give me a message list example !",
          },
          {
            position: "right",
            type: "text",
            title: "Emre",
            text: "That's all.",
          },
        ]}
      />
    </>
  );
}

export default App;
